  FormInputValidator = new FormGroup({
    name: new FormControl('', [Validators.required, Validators.minLength(3)]),
    age: new FormControl(20, [Validators.required,Validators.min(20),Validators.max(40)]),
    email: new FormControl('',[Validators.required, Validators.email])
  });


  get name() { return this.FormInputValidator.get('name') }
  get age() { return this.FormInputValidator.get('age') }
  get email() { return this.FormInputValidator.get('name') }

}
